mapper.select:
SELECT d.p_id AS p_id, d.d_cnt AS p_cnt, d.price AS price, p.p_name as p_name, p.p_img AS p_img, p.del_cost AS del_cost, p.discount AS discount 
FROM detail_order d JOIN product p ON d.p_id = p.p_id 
WHERE o_id = ${o_id};

mapper.update:
UPDATE ordertbl
SET o_status = '주문취소'
WHERE o_id = #{o_id}
